package com.myfun.framework.httpMessageConvert;

import com.alibaba.fastjson.JSON;
import com.alibaba.fastjson.TypeReference;
import com.myfun.framework.httpMessageConvert.base.BaseMapParam;
import com.myfun.framework.session.ThreadLocalHelper;
import com.myfun.repository.param.AbstractEncryParam;
import com.myfun.repository.param.EncryParamIfc;
import com.myfun.utils.BeanUtil;
import com.myfun.utils.StringUtil;
import com.myfun.utils.rijindael.AESHelper;
import com.myfun.utils.rijindael.AESUtf8Helper;
import org.apache.commons.io.IOUtils;
import org.springframework.http.HttpInputMessage;
import org.springframework.http.HttpOutputMessage;
import org.springframework.http.MediaType;
import org.springframework.http.converter.AbstractHttpMessageConverter;
import org.springframework.http.converter.HttpMessageNotReadableException;
import org.springframework.http.converter.HttpMessageNotWritableException;
import org.springframework.stereotype.Component;
import org.springframework.web.context.request.RequestContextHolder;
import org.springframework.web.context.request.ServletRequestAttributes;

import javax.servlet.http.HttpServletRequest;
import java.io.IOException;
import java.nio.charset.Charset;
import java.util.HashMap;
import java.util.Map;
import java.util.Optional;

/**
 * 加密请求转化
 *
 * @author jiangsonggui
 * @ClassName: EncryptionHttpMessageConvert
 * @date 2016年8月4日 上午12:00:05
 */
@Component
public class EncryptionHttpMessageConvert extends AbstractHttpMessageConverter<EncryParamIfc> {
    public static final Charset DEFAULT_CHARSET = Charset.forName("UTF-8");
    public static final Charset GBK_CHARSET = Charset.forName("GBK");

    public EncryptionHttpMessageConvert() {
        super(new MediaType("application", "json", DEFAULT_CHARSET), new MediaType("application", "encrypt-gbk-json", GBK_CHARSET),
                new MediaType("application", "encrypt-json", DEFAULT_CHARSET), new MediaType("application", "noEncryption-json", DEFAULT_CHARSET)
        );
    }


    @Override
    protected boolean supports(Class<?> clazz) {
        return EncryParamIfc.class.isAssignableFrom(clazz);
    }

    @Override
    protected EncryParamIfc readInternal(Class<? extends EncryParamIfc> clazz, HttpInputMessage inputMessage) throws IOException, HttpMessageNotReadableException {
        String jsonStr = IOUtils.toString(inputMessage.getBody(), DEFAULT_CHARSET);
        //System.out.println("message转换器："+jsonStr);
        // 暂时不允许json格式数据，全部是需要加密的，解密失败就不管了
        // 之后会加验证才能使用纯json格式
        //if(!jsonStr.startsWith("{")){
        HttpServletRequest request = ((ServletRequestAttributes) RequestContextHolder.getRequestAttributes()).getRequest();
        try {
            String contentType = request.getHeader("Content-Type");
            if (contentType.contains("application/noEncryption-json") || contentType.contains("application/json")) {
                //此类型参数不用加密
            } else if (contentType.contains("application/encrypt-json")) {
                jsonStr = AESUtf8Helper.decode(jsonStr);
            } else {
                jsonStr = AESHelper.decode(jsonStr);
            }
        } catch (Exception e) {
            e.printStackTrace();
            throw new HttpMessageNotReadableException("Unrecognized field \"xparam\"");
        }
        //}
        EncryParamIfc resultParam;
        ThreadLocalHelper.getACTION_URL_LOCAL().setParams(jsonStr);
        String isMobileApi = inputMessage.getHeaders().getFirst("isMobileApi");
        isMobileApi = Optional.ofNullable(isMobileApi).orElse("0");
        if (BaseMapParam.class.isAssignableFrom(clazz)) {
            Map<String, Object> tempMap = JSON.parseObject(jsonStr, new TypeReference<HashMap<String, Object>>() {
            });
            tempMap.put("isMobileApi", isMobileApi);
            BaseMapParam baseMapParam = new BaseMapParam();
            baseMapParam.setParam(tempMap);
            baseMapParam.setPageOffset(StringUtil.parseInteger(tempMap.get("pageOffset"), null));
            baseMapParam.setPageRows(StringUtil.parseInteger(tempMap.get("pageRows"), null));
            resultParam = baseMapParam;
        } else {
            resultParam = JSON.parseObject(jsonStr, clazz);
            BeanUtil.setProperty(resultParam, "isMobileApi", isMobileApi);
        }
        // 设置字符串到参数
        if (AbstractEncryParam.class.isInstance(resultParam)) {
            AbstractEncryParam tempParam = (AbstractEncryParam) resultParam;
            tempParam.setParamBody(jsonStr);
        }
//        RequestBodyParamThreadLocal.set(jsonStr);
        return resultParam;
    }

    @Override
    protected void writeInternal(EncryParamIfc t, HttpOutputMessage outputMessage) throws IOException, HttpMessageNotWritableException {
        System.out.println("writeInternal");
    }

    public static void main(String[] args) throws Exception {
//        System.out.println(AESHelper.encode("{\"CLIENTKEY\":\"49b8d022c5f761ef61a89b78854f3548\",\"DATE_TYPE\":\"SIGN_DATE\",\"DIMENSION\":\"USER\",\"END_TIME\":\"2018-09-10\",\"PF_ACTUAL\":\"0\",\"SERCH_RANGE\":\"COMP\",\"SERCH_RANGE_ID\":\"\",\"SHOW_TYPE\":\"HOME\",\"START_TIME\":\"2018-09-01\"}\n"));
//
//        System.out.println(AESHelper.decode(""));
//
//        System.out.println(AESHelper.encode("{}"));
        System.out.println(AESHelper.decode(""));


    }
}

