mapper.insert:


  SELECT SCOPE_IDENTITY()

insert into APP_LOGIN_LOG (ARCHIVE_ID, LOGIN_TIME, USER_MOBILE, 
  VERSION_NAME, DEVICE_TYPE, PUSH_TOKEN, 
  LOGIN_STATUS, SIMSERIAL, MOBILE_MODEL
  )
values (#{archiveId,jdbcType=INTEGER}, #{loginTime,jdbcType=TIMESTAMP}, #{userMobile,jdbcType=VARCHAR}, 
  #{versionName,jdbcType=VARCHAR}, #{deviceType,jdbcType=INTEGER}, #{pushToken,jdbcType=VARCHAR}, 
  #{loginStatus,jdbcType=INTEGER}, #{simserial,jdbcType=VARCHAR}, #{mobileModel,jdbcType=VARCHAR}
  )

mapper.select:
SELECT MIN(LOGIN_TIME) AS LOGIN_TIME FROM APP_LOGIN_LOG 
WHERE ARCHIVE_ID = #{archiveId}

	and LOGIN_TIME > #{beginDate}
