mapper.select:

select 

from BUILD_DIC
where PROVINCE_ID = #{provinceId,jdbcType=INTEGER}
  and CITY_ID = #{cityId,jdbcType=INTEGER}
  and SITE_ID = #{siteId,jdbcType=INTEGER}
  and BUILD_ID = #{buildId,jdbcType=INTEGER}
  and BID = #{bid,jdbcType=INTEGER}

mapper.delete:

delete from BUILD_DIC
where PROVINCE_ID = #{provinceId,jdbcType=INTEGER}
  and CITY_ID = #{cityId,jdbcType=INTEGER}
  and SITE_ID = #{siteId,jdbcType=INTEGER}
  and BUILD_ID = #{buildId,jdbcType=INTEGER}
  and BID = #{bid,jdbcType=INTEGER}

mapper.update:

update BUILD_DIC


    SALE_LEASE = #{saleLease,jdbcType=TINYINT},


    BUILD_NAME = #{buildName,jdbcType=VARCHAR},


    THEIR_NAME = #{theirName,jdbcType=VARCHAR},


    BUILD_VALUE = #{buildValue,jdbcType=VARCHAR},


    UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP},


    B_VERIFY = #{bVerify,jdbcType=TINYINT},


    UPDATOR = #{updator,jdbcType=INTEGER},


where PROVINCE_ID = #{provinceId,jdbcType=INTEGER}
  and CITY_ID = #{cityId,jdbcType=INTEGER}
  and SITE_ID = #{siteId,jdbcType=INTEGER}
  and BUILD_ID = #{buildId,jdbcType=INTEGER}
  and BID = #{bid,jdbcType=INTEGER}


update BUILD_DIC
set SALE_LEASE = #{saleLease,jdbcType=TINYINT},
  BUILD_NAME = #{buildName,jdbcType=VARCHAR},
  THEIR_NAME = #{theirName,jdbcType=VARCHAR},
  BUILD_VALUE = #{buildValue,jdbcType=VARCHAR},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP},
  B_VERIFY = #{bVerify,jdbcType=TINYINT},
  UPDATOR = #{updator,jdbcType=INTEGER}
where PROVINCE_ID = #{provinceId,jdbcType=INTEGER}
  and CITY_ID = #{cityId,jdbcType=INTEGER}
  and SITE_ID = #{siteId,jdbcType=INTEGER}
  and BUILD_ID = #{buildId,jdbcType=INTEGER}
  and BID = #{bid,jdbcType=INTEGER}
