mapper.select:
  	select 
	FC.PROVINCE_ID,
	FC.CITY_ID,
	FC.REG_ID,
	FC.COMP_ID,
	FC.SELL_ID,
	FC.COMP_NO,
	FC.CREATION_TIME,
	FC.COMP_WEIXIN_STATUS,
	FC.COMP_NAME,
	WOC.WEIXIN_FLAG,
	WOC.WEIXIN_TOKEN,WOC.WEIXIN_LOGIN_PASS,WOC.WEIXIN_ID,WOC.WEIXIN_CREATION_TIME
	from FUN_COMP FC LEFT JOIN WEIXIN_OPEN_COMP WOC ON FC.COMP_ID = WOC.COMP_ID
	where 1=1

		and FC.CITY_ID=#{cityId}


		and FC.COMP_NAME=#{compName}


		and WOC.WEIXIN_FLAG=#{weixin_Flag}


		and WOC.WEIXIN_STATUS=#{weixin_status}


		and FC.COMP_NO=#{compNo}


		and DEPT.WEIXIN_SERVICE_TIME >= #{startTime}


		and DEPT.WEIXIN_SERVICE_TIME <= #{endTime}

  	order by WEIXIN_CREATION_TIME DESC

select PROVINCE_ID, FC.CITY_ID, REG_ID, FC.COMP_ID, SELL_ID, COMP_NO, CREATION_TIME, COMP_WEIXIN_STATUS, FC.COMP_NAME, WOC.WEIXIN_FLAG, WOC.WEIXIN_TOKEN,WOC.WEIXIN_LOGIN_PASS,WOC.WEIXIN_ID,WOC.WEIXIN_CREATION_TIME, WOC.WEIXIN_CONTACT,WOC.WEIXIN_MOBILE,WOC.WEIXIN_TWO_CONTACT,WOC.WEIXIN_TWO_MOBILE from FUN_COMP FC LEFT JOIN WEIXIN_OPEN_COMP WOC ON FC.COMP_ID = WOC.COMP_ID where 1=1
and FC.COMP_ID=#{compId}
order by WEIXIN_CREATION_TIME DESC

SELECT fc.*,fp.PROVINCE_NAME provinceName,fcity.CITY_NAME cityName from FUN_COMP fc
LEFT JOIN FUN_CITY fcity ON fc.CITY_ID=fcity.CITY_ID 
LEFT JOIN FUN_PROVINCE fp ON fc.PROVINCE_ID=fp.PROVINCE_ID


		and fc.COMP_NO = #{compNo}


		and fc.DISTRIBUTE_MANAGER_FLAG = #{distributeManagerFlag}


		and fc.COMP_VIEW_RANGE = #{compViewRange}


ORDER by CREATION_TIME DESC

select

from FUN_COMP
where COMP_NO = #{compNo} and CREATION_TIME >= #{startTime}
