mapper.insert:

insert into FUN_TRUE_HOUSE_COUNT (CASE_ID, CASE_TYPE, CITY_ID, 
  CASE_NO, CREATION_TIME, COMP_ID, 
  DEPT_ID, COMP_NO, DEPT_NO, 
  ARCHIVE_ID, USER_ID, [STATUS]
  )
values (#{caseId,jdbcType=INTEGER}, #{caseType,jdbcType=TINYINT}, #{cityId,jdbcType=INTEGER}, 
  #{caseNo,jdbcType=VARCHAR}, #{creationTime,jdbcType=TIMESTAMP}, #{compId,jdbcType=INTEGER}, 
  #{deptId,jdbcType=INTEGER}, #{compNo,jdbcType=VARCHAR}, #{deptNo,jdbcType=VARCHAR}, 
  #{archiveId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{status,jdbcType=TINYINT}
  )

mapper.select:
with t1 as (
	select count(1) userCount from (
		select count(1) c from FUN_TRUE_HOUSE_COUNT where creation_time BETWEEN #{queryDate}+' 00:00:00' and #{queryDate}+' 23:59:59' group by archive_id
	) a
),t2 as (
	select count(1) trueCount from FUN_TRUE_HOUSE_COUNT where creation_time BETWEEN #{queryDate}+' 00:00:00' and #{queryDate}+' 23:59:59' and status=1
)
select * from t1,t2

select count(1) trueCount from FUN_TRUE_HOUSE_COUNT a join FUN_ARCHIVE b on b.USER_MOBILE=#{mobile}
where a.archive_id=b.ARCHIVE_ID
and a.creation_time between #{startDate} and #{endDate}
and status=1

select count(1) trueCount from FUN_TRUE_HOUSE_COUNT 
where comp_no=#{compNo} and dept_no=#{deptNo}
and creation_time between #{startDate} and #{endDate} and status=1

select count(1) as counts from FUN_TRUE_HOUSE_COUNT where CITY_ID=#{param.cityId} and COMP_ID=#{param.compId} 

	and CASE_ID = #{param.caseId,jdbcType=INTEGER}


	and CASE_TYPE = #{param.caseType,jdbcType=TINYINT}


	and CITY_ID = #{param.cityId,jdbcType=INTEGER}


	and CASE_NO = #{param.caseNo,jdbcType=VARCHAR}


	and CREATION_TIME >= #{param.startTime,jdbcType=TIMESTAMP}


	and CREATION_TIME <= #{param.endTime,jdbcType=TIMESTAMP}


	and COMP_ID = #{param.compId,jdbcType=INTEGER}


	and DEPT_ID = #{param.deptId,jdbcType=INTEGER}


	and COMP_NO = #{param.compNo,jdbcType=VARCHAR}


	and DEPT_NO = #{param.deptNo,jdbcType=VARCHAR}


	and ARCHIVE_ID = #{param.archiveId,jdbcType=INTEGER}


	and USER_ID = #{param.userId,jdbcType=INTEGER}


	and [STATUS] = #{param.status,jdbcType=TINYINT}

select count(1) as counts,CASE_ID as caseId from FUN_TRUE_HOUSE_COUNT where CITY_ID=#{param.cityId} and COMP_ID=#{param.compId} 

	and CASE_ID = #{param.caseId,jdbcType=INTEGER}


	and CASE_TYPE = #{param.caseType,jdbcType=TINYINT}


	and CITY_ID = #{param.cityId,jdbcType=INTEGER}


	and CASE_NO = #{param.caseNo,jdbcType=VARCHAR}


	and CREATION_TIME >= #{param.startTime,jdbcType=TIMESTAMP}


	and CREATION_TIME <= #{param.endTime,jdbcType=TIMESTAMP}


	and COMP_ID = #{param.compId,jdbcType=INTEGER}


	and DEPT_ID = #{param.deptId,jdbcType=INTEGER}


	and COMP_NO = #{param.compNo,jdbcType=VARCHAR}


	and DEPT_NO = #{param.deptNo,jdbcType=VARCHAR}


	and ARCHIVE_ID = #{param.archiveId,jdbcType=INTEGER}


	and USER_ID = #{param.userId,jdbcType=INTEGER}


	and [STATUS] = #{param.status,jdbcType=TINYINT}

group by CASE_ID
