mapper.insert:


  SELECT SCOPE_IDENTITY()

insert into YOU_PUNISH_TYPE (YCT_COMPLAIN_TYPE, YPT_INTEGRAL, YPT_STOP_DAYS, 
  YPT_DESC, DEL_FLAG, YPT_COMPLAINT_NUMBER, 
  YPT_STOP_OBJECT, YPT_RETURN_OBJECT, YPT_SHARE_FLAG
  )
values (#{yctComplainType,jdbcType=INTEGER}, #{yptIntegral,jdbcType=INTEGER}, #{yptStopDays,jdbcType=INTEGER}, 
  #{yptDesc,jdbcType=VARCHAR}, #{delFlag,jdbcType=TINYINT}, #{yptComplaintNumber,jdbcType=TINYINT}, 
  #{yptStopObject,jdbcType=TINYINT}, #{yptReturnObject,jdbcType=TINYINT}, #{yptShareFlag,jdbcType=TINYINT}
  )

mapper.select:
SELECT  FROM YOU_PUNISH_TYPE WHERE DEL_FLAG=0

	AND YCT_COMPLAIN_TYPE = #{complainType,jdbcType=INTEGER}

ORDER BY YCT_COMPLAIN_TYPE ASC
