mapper.select:
select 

	from CASE_TRACK
	where CITY_ID = #{cityId,jdbcType=INTEGER}
	AND CASE_ID = #{caseId}
	And CASE_TYPE = #{caseType}
	order by TRACK_TIME desc

mapper.update:
update ${tabName} set CREATOR_UID = #{userId}
where CITY_ID = #{cityId}
	And ${tableField} = #{caseId}

update ${tableName} set TRACK_USER = #{track.tradeUser}, TRACK_TIME = #{track.trackTime}, CASE_STATUS = #{track.caseStatus}, TRADE_PRICE = #{track.tradePrice}, TRADE_TIMES = #{track.tradeTimes}, TRADE_USER = #{track.tradeUser}, TRADE_TIME = #{track.tradeTime}
, TRADE_FLAG = '1' ,
FULL_TIME = '2100-01-01'
where CITY_ID = #{cityId} and ${fieldName} = #{caseId}

update ${tableName} set TRACK_USER = #{track.tradeUser}, TRACK_TIME = #{track.trackTime},
 	CASE_STATUS = #{track.caseStatus}, TRADE_FLAG = #{track.tradeFlag},
 	DOWN_TIME = #{track.downTime}, DOWN_TRACKID = #{track.downTrackId}
where  CITY_ID = #{cityId} and ${fieldName} = #{caseId}
