mapper.delete:

delete from VIP_SALE
where CITY_ID = #{cityId,jdbcType=SMALLINT}
  and SALE_ID = #{saleId,jdbcType=INTEGER}

mapper.select:
  select  
	  , (
			SELECT
				CITY_NAME
			FROM
				FUN_CITY
			WHERE
				FUN_CITY.CITY_ID = VIP_SALE.CITY_ID
		) AS CITY_NAME,
		(
			SELECT
				IS_BROKER
			FROM
				WEB_CLIENT
			WHERE
				WEB_CLIENT.CITY_ID = VIP_SALE.CITY_ID
			AND WEB_CLIENT.CLIENT_ID = VIP_SALE.CLIENT_ID
		) AS IS_BROKER
	FROM
		VIP_SALE


			AND CREATION_TIME  <=  #{date2}


			and CREATION_TIME  >=  #{date1}


			AND TRADE_TIME  >=  #{tradeTime1}


			AND TRADE_TIME  <=  #{tradeTime2}


			AND BUILD_ID =#{buildId,jdbcType=INTEGER}


			AND BUILD_NAME like '%${buildName}%'


			AND CLIENT_ID = (SELECT CLIENT_ID FROM WEB_CLIENT WHERE CLIENT_MOBILE = #{clientmobile})


			And CITY_ID = #{cityId,jdbcType=SMALLINT}


			AND HOUSE_ROOM >= #{houseRoom1,jdbcType=TINYINT}


			AND HOUSE_ROOM <= #{houseRoom2,jdbcType=TINYINT}


			And PRICE >=#{price1,jdbcType=DECIMAL}


			And PRICE <=#{price2,jdbcType=DECIMAL}


			AND CASE_STATUS = #{caseStatus,jdbcType=INTEGER}


			AND NEW_COMPLAIN = #{newComplain,jdbcType=TINYINT}


			AND HAS_REFUND = #{hasRefund,jdbcType=TINYINT}


			 AND HOUSE_USEAGE = #{houseUseage,jdbcType=VARCHAR}








			AND HOUSE_FITMENT = #{houseFitment,jdbcType=VARCHAR}


			AND HOUSE_RIGHT = #{houseRight,jdbcType=VARCHAR}


			AND INFO_SOURCE = #{infoSource,jdbcType=TINYINT}


			AND CREATOR_UID =  #{creatorUid,jdbcType=INTEGER}


	ORDER BY TRADE_TIME DESC

    select  
    from VIP_SALE 
    WHERE 1 = 1

		AND CREATION_TIME  >=  #{date1}


		AND CREATION_TIME  <=  #{date2}


		AND CITY_ID = #{cityId}


		AND BUILD_NAME  like '%${buildName}%'


		AND HOUSE_USEAGE = #{houseUseage}


		AND BUILD_ID is null


		AND BUILD_ID is not null

	and BUILD_FLAG = 1
	order by TRADE_TIME DESC

	select 
  	from VIP_SALE

  	where SALE_ID = #{caseId} and CITY_ID = #{cityId}

	select 
  	from VIP_SALE
  	where SALE_ID = #{caseId} and CITY_ID = #{cityId}

  	SELECT count(1) from VIP_SALE
	WHERE CITY_ID = #{cityId} and BUILD_ID = #{buildId}
	and HOUSE_NUM = #{houseNum}
	and HOUSE_ROOM = #{houseRoom} and HOUSE_AREA = #{houseArea}
	and HOUSE_FITMENT = #{houseFitment} and HOUSE_FLOOR = #{houseFloor}
	and CASE_STATUS >= 0 and CASE_STATUS < 3
	and CREATION_TIME > #{creationTime}

select CITY_ID, SALE_ID, BUILD_ID, BUILD_NAME, BUILD_ADDR, BUILD_REGION, REGION_NAME, SECTION_ID, SECTION_NAME, HOUSE_SUBJECT, HOUSE_USEAGE, HOUSE_TYPE, HOUSE_DIRECT, HOUSE_FITMENT, HOUSE_FLOOR, HOUSE_FLOORS, HOUSE_ROOM, HOUSE_HALL, HOUSE_WEI, HOUSE_AREA, PRICE, PRICE_MIN, WX_ID, WF_STATUS, IS_VIP from VIP_SALE where SALE_ID in
#{item}
and CITY_ID = #{cityId,jdbcType=SMALLINT} and INFO_SOURCE = 12
