mapper.select:
select

from ACCOUNT
where COMP_ID = #{param.compId} 

	and PARTNER_ID = #{param.partnerId} 


	and AT_ID = #{param.atId}


	and CREATION_TIME >= #{param.creationTime1}


	and CREATION_TIME <= concat(#{param.creationTime2},' 23:59:59')

order by CREATION_TIME ASC

select top 1

from ACCOUNT
where AT_ID = #{atId} and COMP_ID = #{compId} 

  and PARTNER_ID = #{partnerId} 

order by CREATION_TIME DESC

select

from ACCOUNT
where ACT_ID = #{actId} and COMP_ID = #{compId}

select
top 1

from ACCOUNT
where AT_ID = #{atId,jdbcType=INTEGER}
and COMP_ID = #{compId,jdbcType=INTEGER}
and CREATION_TIME < #{creationTime,jdbcType=VARCHAR}
order by CREATION_TIME DESC

select

from ACCOUNT
where AT_ID = #{atId}
and COMP_ID = #{compId}
and CREATION_TIME > #{creationTime}
order by CREATION_TIME ASC

mapper.update:
