mapper.select:
  	  select a.NUMBER_POOL_KEY,a.BIND_A,a.BIND_X,a.BIND_A_ORG_ID,a.BIND_A_TIME,a.UNBIND_TIME,a.SUBS_ID,
  	  	a.BIND_OPERATE_USER_ID,a.UNBIND_OPERATE_USER_ID,a.BIND_USER_ID
  	  from ALIYUN_PHONE_BIND_HISTORY a left join FUN_USERS b on a.BIND_USER_ID = b.USER_ID
  	  where a.COMP_ID = #{param.compId}

  	  	  and a.BIND_USER_ID = #{param.userId}


  	  	  and a.NUMBER_POOL_KEY = #{param.numberPoolKey}



	  	  		and a.BIND_A_TIME > #{param.startTime}


	  	  		and a.BIND_A_TIME < #{param.endTime}




	  	  		and a.UNBIND_TIME > #{param.startTime}


	  	  		and a.UNBIND_TIME < #{param.endTime}



  	  	and a.BIND_A_ORG_ID = #{param.organizationId}

  	  order by a.ID
