mapper.select:
SELECT A.X_PHONE, A.BIND_A_TIME, A.ORG_ID, A.ORG_NAME, A.ORG_NO, A.USER_ID, A.USER_NAME, A.TELE_PHONE_BILL, A.MONTHLYRENT, A.OTHER_BILL, A.RECORDING, A.ALL_MONEY, A.TOALL_TIME, A.TOALL_COUNT, A.SUFFIX FROM ALIYUN_TELEPHONE_BILL A LEFT JOIN FUN_USERS B ON A.COMP_ID = B.COMP_ID AND A.USER_ID = B.USER_ID INNER JOIN ALIYUN_PHONE C ON A.X_PHONE = C.BIND_X LEFT JOIN FUN_ORGANIZATION D ON A.ORG_ID = D.ORGANIZATION_ID WHERE A.COMP_ID = #{param.compId}
AND A.SUFFIX BETWEEN #{param.suffixStart} AND #{param.suffixEnd}
AND (A.X_PHONE LIKE '%${param.queryWord}%' OR A.ORG_NAME LIKE '%${param.queryWord}%' OR A.ORG_NO LIKE '%${param.queryWord}%' OR A.USER_NAME LIKE '%${param.queryWord}%')
AND B.TISSUE_LINE LIKE '%:${param.organizationId}:%'
AND D.ORGANIZATION_ID LIKE '%:${param.poolOrganizationId}:%'
AND A.USER_ID = #{param.userId}
ORDER BY A.SUFFIX DESC, A.TOALL_TIME DESC

SELECT ISNULL(SUM(A.ALL_MONEY),0) as totalAllMoney, ISNULL(SUM(A.TOALL_TIME),0) as totalToallTime, ISNULL(SUM(A.MONTHLYRENT),0) as totalMonthlyrent, ISNULL(SUM(A.TELE_PHONE_BILL),0) as totalTelePhoneBill, ISNULL(SUM(A.OTHER_BILL),0) as totalOtherBill, ISNULL(SUM(A.RECORDING),0) as totalRecording FROM ALIYUN_TELEPHONE_BILL A LEFT JOIN FUN_USERS B ON A.COMP_ID = B.COMP_ID AND A.USER_ID = B.USER_ID INNER JOIN ALIYUN_PHONE C ON A.X_PHONE = C.BIND_X LEFT JOIN FUN_ORGANIZATION D ON A.ORG_ID = D.ORGANIZATION_ID WHERE A.COMP_ID = #{param.compId}
AND A.SUFFIX BETWEEN #{param.suffixStart} AND #{param.suffixEnd}
AND (A.X_PHONE LIKE '%${param.queryWord}%' OR A.ORG_NAME LIKE '%${param.queryWord}%' OR A.ORG_NO LIKE '%${param.queryWord}%' OR A.USER_NAME LIKE '%${param.queryWord}%')
AND B.TISSUE_LINE LIKE '%:${param.organizationId}:%'
AND D.ORGANIZATION_ID LIKE '%:${param.poolOrganizationId}:%'
AND A.USER_ID = #{param.userId}
