mapper.select:
select

from BUILD_PERMISSIONS_TRACK
WHERE COMP_ID = #{param.compId}

    AND ORGANIZATION_ID = #{param.organizationId}


    AND CREATION_UID = #{param.userId}


    AND (BUILD_IDS_VIEW LIKE '%${param.buildId}%' OR BUILD_IDS_EDIT LIKE '%${param.buildId}%' OR BUILD_IDS_ADD LIKE '%${param.buildId}%')


    AND CREATION_TIME BETWEEN #{param.creationTimeStrD} AND #{param.creationTimeEndD}

ORDER BY CREATION_TIME DESC
