    AND BUILDING_SET_ROOM_ID = #{param.buildingSetRoomId,jdbcType=INTEGER}


    AND BUILDING_SET_ROOM_ID IN

        #{item}

mapper.select:
SELECT ID AS buildingSetRoomLayoutId, BUILDING_SET_ROOM_ID AS buildingSetRoomId, LAYOUT_URL AS layoutUrl, UPDATED_TIME AS updatedTime
FROM BUILDING_SET_ROOM_LAYOUT
WHERE COMP_ID = 0

    AND BUILD_ID = #{buildId,jdbcType=INTEGER}



    AND BUILDING_SET_ROOM_ID = #{buildingSetRoomId,jdbcType=INTEGER}

