mapper.select:
    select 
	FINE_DEST,COUNT(1) as FINE_COUNT,SUM(FINE_MOUNT) as FINE_SUM  
    from FINE
    where COMP_ID = #{param.compId,jdbcType=INTEGER}

      and  DEPT_ID = #{param.deptId,jdbcType =INTEGER}


     and FINE_TIME <= #{param.statEnd,jdbcType=VARCHAR}


     and FINE_TIME >= #{param.statStart,jdbcType=VARCHAR}

    group by FINE_DEST ORDER BY FINE_DEST OFFSET 0 ROW FETCH NEXT 50 ROWS ONLY

	select 

   from FINE
   where FINE_TIME <= #{param.statEnd,jdbcType=VARCHAR}
   and FINE_TIME >= #{param.statStart,jdbcType=VARCHAR}
   and FINE_DEST =#{param.fineDest,jdbcType=INTEGER}

mapper.insert:


  SELECT next value for SEQ_FINE_FINE_ID

insert into FINE (FINE_ID, COMP_ID, FINE_MOUNT, 
  DEPT_ID, FINE_DEST, FINE_SOURCE, 
  FINE_DESC, FINE_TIME, DISP_TIME
  )
values (#{fineId,jdbcType=INTEGER}, #{compId,jdbcType=INTEGER}, #{fineMount,jdbcType=INTEGER}, 
  #{deptId,jdbcType=INTEGER}, #{fineDest,jdbcType=INTEGER}, #{fineSource,jdbcType=INTEGER}, 
  #{fineDesc,jdbcType=VARCHAR}, #{fineTime,jdbcType=VARCHAR}, #{dispTime,jdbcType=VARCHAR}
  )
