mapper.select:
select
B.LEASE_ID AS CASE_ID,A.ADS_CONTENT_ID,A.USER_ID, A.CREATION_TIME, A.ADS_CONTENT, A.CASE_TYPE, B.PRICE_UNIT AS PRICE_UNIT, A.RECOMMEND_PRICE, A.PRICE_UNIT PRICE_UNIT2, A.NEWSPAPPER_TEL, B.USER_ID AS INFO_OWNER_ID,B.BUILD_NAME,B.CHECK_CODE_FUN AS CHECK_CODE_FUN, B.LEASE_USEAGE,B.LEASE_ROOM AS ROOM,B.LEASE_FLOOR as FLOOR,B.LEASE_AREA AS AREA,B.LEASE_FLOORS AS FLOORS, B.LEASE_TOTAL_PRICE AS TOTAL_PRICE, B.LEASE_FITMENT AS FITMENT,B.LEASE_HALL AS HALL, B.LEASE_WEI AS WEI
B.SALE_ID AS CASE_ID,A.ADS_CONTENT_ID,A.USER_ID,A.CREATION_TIME,A.ADS_CONTENT,A.CASE_TYPE,B.SALE_USEAGE,B.SALE_FLOOR as FLOOR,B.SALE_AREA AS AREA,B.SALE_FLOORS AS FLOORS, B.SALE_TOTAL_PRICE AS TOTAL_PRICE, B.SALE_FITMENT AS FITMENT,B.SALE_ROOM AS ROOM,B.SALE_HALL AS HALL,B.CHECK_CODE_FUN AS CHECK_CODE_FUN, A.RECOMMEND_PRICE,A.PRICE_UNIT PRICE_UNIT2, A.NEWSPAPPER_TEL,B.SALE_WEI AS WEI,B.USER_ID AS INFO_OWNER_ID,B.BUILD_NAME
from FUN_ADS_CONTENT A
LEFT JOIN FUN_SALE B ON A.CASE_ID = B.SALE_ID
LEFT JOIN FUN_LEASE B ON A.CASE_ID = B.LEASE_ID
JOIN FUN_USERS C ON C.USER_ID=A.USER_ID
AND B.USER_ID = #{pMap.userId}
AND B.DEPT_ID = #{pMap.deptId}
AND A.USER_ID = #{pMap.userId}
AND C.DEPT_ID = #{pMap.deptId}
AND C.TISSUE_LINE LIKE '%:${param.organizationId}:%'
AND A.CASE_TYPE = #{pMap.caseType} and A.COMP_ID =#{pMap.compId} and A.ADS_STATUS=1
AND A.CREATION_TIME >= #{pMap.date1}
AND A.CREATION_TIME <= #{pMap.date2}
and B.SALE_STATUS NOT IN (5,6,7)
and B.LEASE_STATUS NOT IN (5,6,7)
order by A.CREATION_TIME DESC

    SELECT COUNT (*) AS ATTR_NAME FROM FUN_ADS_CONTENT WITH (NOLOCK) 
    WHERE 
    COMP_ID = #{map.compId,jdbcType=INTEGER}
	AND DEPT_ID = #{map.deptId,jdbcType=INTEGER}
	AND ADS_STATUS = '1'

mapper.update:
update FUN_ADS_CONTENT
set ADS_STATUS='0',
 CANCLE_TIME = #{map.cancleTime} 
where CASE_TYPE = #{map.caseType}  
and CASE_ID = #{map.caseId}
and COMP_ID = #{map.compId}

update FUN_ADS_CONTENT
set ADS_STATUS='0',
CANCLE_TIME = #{map.cancleTime}
where ADS_CONTENT_ID in

    #{adsId}

update FUN_ADS_CONTENT
set ADS_STATUS='0',
CANCLE_TIME = #{map.cancleTime}
where ADS_CONTENT_ID = #{map.adsContentId}

