mapper.select:
select CONCAT(AREA_ID,'_',REG_ID,'_',DEPT_ID,'_',GR_ID,'_',USER_ID) as range, AREA_ID as areaId,REG_ID as regId,DEPT_ID as deptId,GR_ID as grId,USER_ID as userId, sum(iif(OFF_WORK=0 or (OFF_WORK=2 and (FIRST_WORK_ON != 0 or SECOND_WORK_ON !=0 or THREE_WORK_ON !=0 or FIRST_WORK_OFF != 0 or SECOND_WORK_OFF !=0 or THREE_WORK_OFF !=0)),1,0)) as attendanceDays,sum(iif(OFF_WORK=1,1,0)) as restDays, sum(iif(OFF_WORK=0 and (FIRST_WORK_OFF in (1) or SECOND_WORK_OFF in (1) or THREE_WORK_OFF in (1)),1,0) ) as leaveEarlyCount, sum(iif(OFF_WORK=0 and (FIRST_WORK_ON in (2) or SECOND_WORK_ON in (2) or THREE_WORK_ON in (2)),1,0)) as lateCount, sum(iif(OFF_WORK=0 and (FIRST_WORK_ON = 6 or SECOND_WORK_ON=6 or THREE_WORK_ON =6),1,0)) as seriouslyLateCount, sum(iif(OFF_WORK=0 and (FIRST_WORK_ON = 8 or SECOND_WORK_ON=8 or THREE_WORK_ON =8),1,0)) as lateAbsenteeismCount, sum(iif(OFF_WORK=0 and (FIRST_WORK_OFF = 7 or SECOND_WORK_OFF=7 or THREE_WORK_OFF =7),1,0)) as leaveEarlyAbsenteeismCount, sum(iif(OFF_WORK=0 and ATT_SUM_COUNT=1,iif(FIRST_WORK_ON=0 and FIRST_WORK_ONTIME<='${currentDate}',1,0),iif(OFF_WORK=0 and ATT_SUM_COUNT=2,(iif(FIRST_WORK_ON=0 and FIRST_WORK_ONTIME<='${currentDate}',1,0)+iif(SECOND_WORK_ON=0 and SECOND_WORK_ONTIME<='${currentDate}',1,0)),iif(ATT_SUM_COUNT=3 and OFF_WORK=0 ,(iif(FIRST_WORK_ON=0 and FIRST_WORK_ONTIME<='${currentDate}',1,0)+iif(SECOND_WORK_ON=0 and SECOND_WORK_ONTIME<='${currentDate}',1,0)+iif(THREE_WORK_ON=0 and THREE_WORK_ONTIME<='${currentDate}',1,0)),0)))) as missingAttOnCount, sum(iif(OFF_WORK=0 and ATT_SUM_COUNT=1,iif(FIRST_WORK_OFF=0 and FIRST_WORK_OFFTIME<='${lastMissCardDate}',1,0),iif(OFF_WORK=0 and ATT_SUM_COUNT=2,(iif(FIRST_WORK_OFF=0 and FIRST_WORK_OFFTIME<='${currentDate}',1,0)+iif(SECOND_WORK_OFF=0 and SECOND_WORK_OFFTIME<='${lastMissCardDate}',1,0)),iif(ATT_SUM_COUNT=3 and OFF_WORK=0 ,(iif(FIRST_WORK_OFF=0 and FIRST_WORK_OFFTIME<='${currentDate}',1,0)+iif(SECOND_WORK_OFF=0 and SECOND_WORK_OFFTIME<='${currentDate}',1,0)+iif(THREE_WORK_OFF=0 and THREE_WORK_OFFTIME<='${lastMissCardDate}',1,0)),0)))) as missingAttOffCount, SUM (iif ( OFF_WORK = 3 AND ATT_DATE <'${currentAttDate}',iif (ATT_SUM_COUNT = 1 AND FIRST_WORK_ON = 0 AND FIRST_WORK_OFF = 0,1, iif (ATT_SUM_COUNT = 2 AND FIRST_WORK_ON = 0 AND FIRST_WORK_OFF = 0 AND SECOND_WORK_ON = 0 AND SECOND_WORK_OFF = 0,1,iif ( ATT_SUM_COUNT = 3 AND FIRST_WORK_ON = 0 AND FIRST_WORK_OFF = 0 AND SECOND_WORK_ON = 0 AND SECOND_WORK_OFF = 0 AND THREE_WORK_ON = 0 AND THREE_WORK_OFF = 0,1,0 ))),0)) as missingAttCount, SUM (iif (OFF_WORK = 0 AND ATT_SUM_COUNT = 1,iif (FIRST_WORKON_TYPE = 2 AND FIRST_WORK_ON IN (2,4,6),1,0) + iif (FIRST_WORKOFF_TYPE = 2 AND FIRST_WORK_OFF IN (2,4,6),1,0 ),iif ( OFF_WORK = 0 AND ATT_SUM_COUNT = 2, (iif (FIRST_WORKON_TYPE = 2 AND FIRST_WORK_ON IN (2,4,6),1,0) + iif (FIRST_WORKOFF_TYPE = 2 AND FIRST_WORK_OFF IN (2,4,6),1,0) + iif (SECOND_WORKON_TYPE = 2 AND SECOND_WORK_ON IN (2,4,6),1,0) + iif (SECOND_WORKOFF_TYPE = 2 AND SECOND_WORK_OFF IN (2,4,6),1,0)),iif (ATT_SUM_COUNT = 3 AND OFF_WORK = 0,(iif (FIRST_WORKON_TYPE = 2 AND FIRST_WORK_ON IN (2,4,6),1,0) + iif (FIRST_WORKOFF_TYPE = 2 AND FIRST_WORK_OFF IN (2,4,6),1,0) + iif (SECOND_WORKON_TYPE = 2 AND SECOND_WORK_ON IN (2,4,6),1,0) + iif (SECOND_WORKOFF_TYPE = 2 AND SECOND_WORK_OFF IN (2,4,6),1,0) + iif (THREE_WORKOFF_TYPE = 2 AND THREE_WORK_OFF IN (2,4,6),1,0) + iif (THREE_WORKON_TYPE = 2 AND THREE_WORK_ON IN (2,4,6),1,0)),0)))) AS outAttCount from FUN_ATTENDANCE_STATISTICS where COMP_ID = #{param.compId}
and ${param.searchRange} = #{param.searchRangeId}
AND ORGANIZATION_PATH LIKE '%:${param.organizationId}:%'
AND USER_ID = #{param.userId}
and ATT_DATE >= #{param.startTime} and ATT_DATE <= #{param.endTime} group by CONCAT(AREA_ID,'_',REG_ID,'_',DEPT_ID,'_',GR_ID,'_',USER_ID), AREA_ID,REG_ID,DEPT_ID,GR_ID,USER_ID

