ND ATT_DATE <'${currentAttDate}',iif (ATT_SUM_COUNT = 1 AND FIRST_WORK_ON = 0 AND FIRST_WORK_OFF = 0,1, iif (ATT_SUM_COUNT = 2 AND FIRST_WORK_ON = 0 AND FIRST_WORK_OFF = 0 AND SECOND_WORK_ON = 0 AND SECOND_WORK_OFF = 0,1,iif ( ATT_SUM_COUNT = 3 AND FIRST_WORK_ON = 0 AND FIRST_WORK_OFF = 0 AND SECOND_WORK_ON = 0 AND SECOND_WORK_OFF = 0 AND THREE_WORK_ON = 0 AND THREE_WORK_OFF = 0,1,0 ))),0)) as missingAttCount, SUM (iif (OFF_WORK = 0 AND ATT_SUM_COUNT = 1,iif (FIRST_WORKON_TYPE = 2 AND FIRST_WORK_ON IN (2,4,6),1,0) + iif (FIRST_WORKOFF_TYPE = 2 AND FIRST_WORK_OFF IN (2,4,6),1,0 ),iif ( OFF_WORK = 0 AND ATT_SUM_COUNT = 2, (iif (FIRST_WORKON_TYPE = 2 AND FIRST_WORK_ON IN (2,4,6),1,0) + iif (FIRST_WORKOFF_TYPE = 2 AND FIRST_WORK_OFF IN (2,4,6),1,0) + iif (SECOND_WORKON_TYPE = 2 AND SECOND_WORK_ON IN (2,4,6),1,0) + iif (SECOND_WORKOFF_TYPE = 2 AND SECOND_WORK_OFF IN (2,4,6),1,0)),iif (ATT_SUM_COUNT = 3 AND OFF_WORK = 0,(iif (FIRST_WORKON_TYPE = 2 AND FIRST_WORK_ON IN (2,4,6),1,0) + iif (FIRST_WORKOFF_TYPE = 2 AND FIRST_WORK_OFF IN (2,4,6),1,0) + iif (SECOND_WORKON_TYPE = 2 AND SECOND_WORK_ON IN (2,4,6),1,0) + iif (SECOND_WORKOFF_TYPE = 2 AND SECOND_WORK_OFF IN (2,4,6),1,0) + iif (THREE_WORKOFF_TYPE = 2 AND THREE_WORK_OFF IN (2,4,6),1,0) + iif (THREE_WORKON_TYPE = 2 AND THREE_WORK_ON IN (2,4,6),1,0)),0)))) AS outAttCount from FUN_ATTENDANCE_STATISTICS where COMP_ID = #{param.compId}
