SELECT
TOP ${param.topCount}
COUNT(1) AS counts,


        ${param.dimensionColumnSql} AS dim



        AS dim




        FROM FUN_BUY_CUSTOMER a
        left join FUN_CUST_REGSECTIONS b on b.case_id=a.buy_cust_id and b.case_type=3 and b.SOURCE_TYPE=0
        left join FUN_CUST_REGSECTIONS c on c.case_id=a.buy_cust_id and c.case_type=3 and c.SOURCE_TYPE=1
        WHERE a.COMP_ID=#{param.compId}
        AND a.${param.serchRange}=#{param.serchRangeId}
        AND BUY_CUST_STATUS IN (1,2,4)


        FROM FUN_BUY_CUSTOMER a
        left join FUN_CUST_REGSECTIONS b on b.case_id=a.buy_cust_id and b.case_type=3 and b.SOURCE_TYPE=0
        WHERE a.COMP_ID=#{param.compId}
        AND a.${param.serchRange}=#{param.serchRangeId}
        AND BUY_CUST_STATUS IN (1,2,4)


        FROM FUN_BUY_CUSTOMER a
        left join FUN_CUST_DEMAND_SPLIT b on b.case_id=a.buy_cust_id and b.case_type=3 and b.SOURCE_TYPE=2

            left join FUN_CUST_REGSECTIONS c on c.case_id=a.buy_cust_id and c.case_type=3 and c.SOURCE_TYPE=0


            left join FUN_CUST_REGSECTIONS d on d.case_id=a.buy_cust_id and d.case_type=3 and d.SOURCE_TYPE=1

        WHERE a.COMP_ID=#{param.compId}
        AND a.${param.serchRange}=#{param.serchRangeId}
        AND BUY_CUST_STATUS IN (1,2,4)
        AND c.SOURCE_ID = #{param.regionId}

        AND d.SOURCE_ID = #{param.sectionId}



        FROM FUN_BUY_CUSTOMER a

            left join FUN_CUST_REGSECTIONS c on c.case_id=a.buy_cust_id and c.case_type=3 and c.SOURCE_TYPE=0


            left join FUN_CUST_REGSECTIONS d on d.case_id=a.buy_cust_id and d.case_type=3 and d.SOURCE_TYPE=1

        WHERE a.COMP_ID=#{param.compId}
        AND a.${param.serchRange}=#{param.serchRangeId}
        AND a.BUY_CUST_STATUS IN (1,2,4)
        AND c.SOURCE_ID = #{param.regionId}

        AND d.SOURCE_ID = #{param.sectionId}



GROUP BY


        ${param.dimensionGroupSql}





having COUNT(1) > 0
ORDER BY COUNT(1) DESC

