with T_SIGN_USER as ( select distinct B.AREA_ID,B.REG_ID,B.DEPT_ID,B.DEAL_ID,A.SIGN_USER_ID,A.SIGN_DATE,A.TRANSFER_DATE,A.DEAL_VERIFY_TIME from FUN_DEAL A join DEAL_USER_RELATIVE B ON A.DEAL_ID=B.DEAL_ID AND A.SIGN_USER_ID=B.USER_ID AND A.COMP_ID = #{param.compId} where B.COMP_ID = #{param.compId} AND A.ISBYHAND = 0 AND A.DEAL_AUDIT_STATUS != 3 AND B.RELATE_TYPE = 3
AND ((A.SIGN_DATE >= #{param.countDateStart} AND A.SIGN_DATE <= #{param.countDateEnd}) OR (${param.dateType} >= #{param.countDateStart} AND ${param.dateType} <= #{param.countDateEnd}))
AND B.AREA_ID = #{param.areaId}
AND B.REG_ID = #{param.regId}
AND B.DEPT_ID = #{param.deptId}
and B.ORGANIZATION_PATH LIKE '%:${param.organizationId}:%'
), T_FUN_PROCESS as ( select * from (SELECT A.DEAL_ID,('A_'+convert(varchar(10),B.PAYMENT_ID) + '_COUNT') PAYMENT_ID,SUM(B.PAY_AMOUNT) PAY_AMOUNT FROM T_SIGN_USER A LEFT JOIN PROCESS_FINANC B ON A.DEAL_ID = B.DEAL_ID WHERE A.DEAL_ID > 0 and B.PF_ACTUAL=1 GROUP BY A.DEAL_ID,B.PAYMENT_ID ) p PIVOT ( SUM([PAY_AMOUNT]) FOR [PAYMENT_ID] IN (${param.buildPaymentIdString} ) ) AS pvt ) select A.AREA_ID as areaId,A.REG_ID as regId,A.DEPT_ID as deptId,sum(iif(DATEPART(month,A.SIGN_DATE)=DATEPART(month, ${param.dateType}) and DATEPART(yyyy,A.SIGN_DATE)=DATEPART(yyyy,${param.dateType}) and (convert(VARCHAR(8),${param.dateType},20)+'01') = #{param.nowDateStr},${param.transfer},0)) as paymentTransfer2, sum(iif(A.SIGN_DATE < (convert(VARCHAR(8),${param.dateType},20)+'01') and (convert(VARCHAR(8),${param.dateType},20)+'01') = #{param.nowDateStr},#{param.transfer},0)) as paymentTransfer1, sum(iif(DATEPART(month,A.SIGN_DATE)=DATEPART(month,${param.dateType}) and DATEPART(yyyy,A.SIGN_DATE)=DATEPART(yyyy,${param.dateType}) and (convert(VARCHAR(8), ${param.dateType},20)+'01') = #{param.nowDateStr},${param.commission},0)) as paymentCommission2, sum(iif(A.SIGN_DATE < (convert(VARCHAR(8),${param.dateType},20)+'01') and (convert(VARCHAR(8),${param.dateType},20)+'01') = #{param.nowDateStr},${param.commission},0)) as paymentCommission1, sum(iif(A.SIGN_USER_ID > 0,iif(DATEPART(month,A.SIGN_DATE)=DATEPART(month,${param.dateType}) and DATEPART(yyyy,A.SIGN_DATE)=DATEPART(yyyy,${param.dateType}) and (convert(VARCHAR(8), ${param.dateType},20)+'01') = #{param.nowDateStr},1,0 ),0)) as dealCount2, sum(iif(A.SIGN_USER_ID > 0,iif(A.SIGN_DATE < (convert(VARCHAR(8),${param.dateType},20)+'01') and (convert(VARCHAR(8),${param.dateType},20)+'01') = #{param.nowDateStr},1,0 ),0)) as dealCount1, sum(iif(A.SIGN_USER_ID > 0 and (convert(VARCHAR(8),A.SIGN_DATE,20)+'01') = #{param.nowDateStr},1,0 )) as dealCount3 from T_SIGN_USER A left join T_FUN_PROCESS B ON A.DEAL_ID = B.DEAL_ID where A.SIGN_USER_ID > 0 group by A.AREA_ID,A.REG_ID,A.DEPT_ID

with T_FUN_PROFIT AS (select * from ( SELECT A.COMP_ID,A.AREA_ID,A.REG_ID,A.DEPT_ID,A.GR_ID,A.USER_ID,A.ARCHIVE_ID,A.DEAL_ID, (convert(varchar(10),A.PERFORMANCE_ID) + 'Count') PERFORMANCE_ID,SUM(A.PROFIT_MONEY) PROFIT_MONEY FROM FUN_PROFIT A WHERE A.COMP_ID = #{param.compId} GROUP BY A.COMP_ID,A.AREA_ID,A.REG_ID,A.DEPT_ID,A.GR_ID,A.USER_ID,A.ARCHIVE_ID,A.DEAL_ID,A.PERFORMANCE_ID ) p PIVOT ( SUM([PROFIT_MONEY]) FOR [PERFORMANCE_ID] IN (${param.performanceColumn[0]}) ) AS pvt),T_FUN_PROFIT_COUNT AS (SELECT A.COMP_ID,A.DEPT_ID,A.USER_ID,A.ARCHIVE_ID,A.DEAL_ID,SUM (A.PROFIT_MONEY) PROFIT_MONEY, SUM(A.PROFIT_PROPORTION) PROFIT_PROPORTION FROM FUN_PROFIT A GROUP BY A.COMP_ID,A.DEPT_ID,A.USER_ID,A.ARCHIVE_ID,A.DEAL_ID),T_FUN_PROFIT_PROPORTION AS (select * from ( SELECT A.COMP_ID,A.AREA_ID,A.REG_ID,A.DEPT_ID,A.GR_ID,A.USER_ID,A.ARCHIVE_ID,A.DEAL_ID, (convert(varchar(10),PERFORMANCE_ID) + 'RatioCount') RATIO_COUNT,sum(PROFIT_PROPORTION) PROFIT_PROPORTION FROM FUN_PROFIT A WHERE A.COMP_ID = #{param.compId} GROUP BY A.COMP_ID,A.AREA_ID,A.REG_ID,A.DEPT_ID,A.GR_ID,A.USER_ID,A.ARCHIVE_ID,A.DEAL_ID,A.PERFORMANCE_ID ) p PIVOT ( SUM([PROFIT_PROPORTION]) FOR [RATIO_COUNT] IN (${param.performanceColumn[1]}) ) AS pvt) SELECT ${param.performanceColumn[0]},${param.performanceColumn[1]},D.COMP_ID compId,D.AREA_ID areaId,D.REG_ID regId,D.DEPT_ID deptId,ISNULL(D.GR_ID, 0) AS grId,D.USER_ID userId,D.ARCHIVE_ID archiveId,A.DEAL_TYPE dealType,A.SELL_ADDRESS sellAddress,A.CATEGORY_NAME categoryName, A.BUY_MORTGAGE_TYPE buyMortgageType,A.CATEGORY_ID categoryId, A.DEAL_ID dealId,A.ISBYHAND isbyhand, A.CONTRACT_NO contractNo, A.DEAL_HOUSE_NO dealHouseNo,A.HOUSEADDRESS houseaddress,A.SIGN_DATE signDate,A.PAYTYPE_NAME paytypeName,A.TRANSFER_DATE transferDate, A.RENT_PAY_METHOD rentPayMethod,A.SELL_DEAL_MONEY sellDealMoney, A.TOTAL_NEED_PERFORMANCE ysPaymoney,A.TOTAL_REAL_PERFORMANCE realPaymoney, F.PROFIT_PROPORTION fcRate,F.PROFIT_MONEY fcProfit FROM T_FUN_PROFIT D LEFT JOIN FUN_DEAL A ON D.DEAL_ID=A.DEAL_ID LEFT JOIN T_FUN_PROFIT_COUNT F ON A.DEAL_ID = F.DEAL_ID AND (F.USER_ID = D.USER_ID OR (F.USER_ID IS NULL AND D.USER_ID IS NULL AND F.DEPT_ID = D.DEPT_ID) OR (F.DEPT_ID IS NULL AND D.DEPT_ID IS NULL)) LEFT JOIN T_FUN_PROFIT_PROPORTION G ON A.DEAL_ID = G.DEAL_ID AND (G.USER_ID = D.USER_ID OR (G.USER_ID IS NULL AND D.USER_ID IS NULL AND G.DEPT_ID = D.DEPT_ID) OR (G.DEPT_ID IS NULL AND D.DEPT_ID IS NULL)) WHERE D.COMP_ID=#{param.compId}
AND A.${param.dateType} >= #{param.startTime}
AND A.${param.dateType} < #{param.endTime}
AND (A.CONTRACT_NO LIKE #{param.contractNo} OR A.AGREEMENT_NO LIKE #{param.contractNo})
AND A.DEAL_TYPE = #{param.dealType}
AND ${param.serchRange} = #{param.serchRangeId}
AND A.CATEGORY_ID is null
AND A.CATEGORY_ID =#{param.categoryId}
ORDER BY D.COMP_ID,D.AREA_ID,D.REG_ID,D.DEPT_ID,ISNULL(D.GR_ID, IIF(D.USER_ID is null, 2100000000, 0)),ISNULL(D.USER_ID,2100000000)

with T_FUN_PROFIT AS (
    select * from (
      SELECT ${param.queryColStr} AS range,
      D.ARCHIVE_ID,D.DEAL_ID,(convert(varchar(10),D.PERFORMANCE_ID) + 'Count') PERFORMANCE_ID,
      SUM(A.PROFIT_MONEY) PROFIT_MONEY
      FROM FUN_PROFIT D
      WHERE D.COMP_ID = #{param.compId}

        AND ${param.whereStr} IS NOT NULL

      GROUP BY ${param.groupBy},D.ARCHIVE_ID,D.DEAL_ID,D.PERFORMANCE_ID
    ) p PIVOT ( SUM([PROFIT_MONEY]) FOR [PERFORMANCE_ID] IN (${param.performanceColumn[0]}) ) AS pvt
),T_FUN_PROFIT_COUNT AS (
    SELECT ${param.queryColStr} AS range,D.ARCHIVE_ID,D.DEAL_ID,
    SUM (D.PROFIT_MONEY) PROFIT_MONEY,
    SUM(D.PROFIT_PROPORTION) PROFIT_PROPORTION
    FROM FUN_PROFIT D
    WHERE D.COMP_ID = #{param.compId}

      AND ${param.whereStr} IS NOT NULL

    GROUP BY ${param.groupBy},D.ARCHIVE_ID,D.DEAL_ID
),T_FUN_PROFIT_PROPORTION AS (
  select * from (
    SELECT ${param.queryColStr} AS range,
    D.ARCHIVE_ID,D.DEAL_ID,(convert(varchar(10),PERFORMANCE_ID) + 'RatioCount') RATIO_COUNT,
    sum(PROFIT_PROPORTION) PROFIT_PROPORTION
    FROM FUN_PROFIT D
    WHERE D.COMP_ID = #{param.compId}

      AND ${param.whereStr} IS NOT NULL

    GROUP BY ${param.groupBy},D.ARCHIVE_ID,D.DEAL_ID,D.PERFORMANCE_ID
  ) p PIVOT ( SUM([PROFIT_PROPORTION]) FOR [RATIO_COUNT] IN (${param.performanceColumn[1]}) ) AS pvt
)
SELECT ${param.performanceColumn[0]},${param.performanceColumn[1]},
  ${param.queryColStr} AS range,D.ARCHIVE_ID archiveId,A.DEAL_TYPE dealType,A.SELL_ADDRESS sellAddress,A.CATEGORY_NAME categoryName,
A.BUY_MORTGAGE_TYPE buyMortgageType,A.CATEGORY_ID categoryId, A.DEAL_ID dealId,A.ISBYHAND isbyhand, A.CONTRACT_NO contractNo,
A.DEAL_HOUSE_NO dealHouseNo,A.HOUSEADDRESS houseaddress,A.SIGN_DATE signDate,A.PAYTYPE_NAME paytypeName,A.TRANSFER_DATE transferDate,
