select A.DEAL_ID,A.CREATOR_DATE,B.PERFORMANCE_TYPE,A.SELL_OWNER_NAME,A.BUY_OWNER_NAME,B.PF_PAYER,B.PF_GETER,B.PAY_AMOUNT,B.PF_TIME,A.REMARK,A.AGREEMENT_NO,
