  CREATE_TIME = #{createTime,jdbcType=VARCHAR},
  FRIEND_ARCHIVE_SEX = #{friendArchiveSex,jdbcType=INTEGER},
  FRIEND_ARCHIVE_NAME = #{friendArchiveName,jdbcType=VARCHAR},
  GROUP_NAME = #{groupName,jdbcType=VARCHAR},
  FRIEND_ARCHIVE_PHOTO = #{friendArchivePhoto,jdbcType=VARCHAR}
where ARCHIVE_ID = #{archiveId,jdbcType=INTEGER}
  and FRIEND_ARCHIVE_ID = #{friendArchiveId,jdbcType=INTEGER}
