mapper.select:
select

from FUN_HISTORY_DEAL_CUST



        and BELONG_ORG_PATH like concat(concat('%',#{orgId}),'%')


      and BELONG_USER_ID = #{param.userId}




      and DEAL_ORG_PATH like concat(concat('%',#{orgId}),'%')


      and DEAL_USER_ID = #{param.userId}



        and DEAL_TYPE = #{param.dealType}


    and BUILD_ID = #{param.buildId}


    and DEAL_TIME >= #{param.dealStartTime}


    and DEAL_TIME <= #{param.dealEndTime}


    and (
        CUST_NAME like concat(concat('%',#{keyWords}),'%')
    or CUST_TEL like concat(concat('%',#{keyWords}),'%')
    or CASE_NUM like concat(concat('%',#{keyWords}),'%')
    )


ORDER BY FUN_HISTORY_DEAL_CUST_ID
