mapper.select:
select DISTINCT A.LEASE_ID from FUN_LEASE A
JOIN FUN_LEASE_PUBLISH_INFO F ON A.LEASE_ID=F.LEASE_ID
WHERE F.WEBSITE_STATE='1' AND A.USER_ID=#{userId}

