mapper.select:
SELECT MAPPING_DB_FIELD FROM dbo.FUN_ORGANIZATION_DEFINITION
WHERE COMP_ID = #{param.compId}

  AND DEFINITION_LEVEL <= #{param.level}

ORDER BY DEFINITION_LEVEL

select a.* from FUN_ORGANIZATION_DEFINITION a
join FUN_ORGANIZATION b on a.DEFINITION_ID = b.ORGANIZATION_DEFINITION_ID
where b.ORGANIZATION_ID = #{organizationId}
