mapper.select:
select distinct
A.ID, A.COMP_ID, A.PARTNER_NAME, A.ENTERPRISE_CREDIT_CODE, A.JOIN_TIME, A.USER_ID, A.MANAGER_USER_ID,A.CREATE_TIME,A.PARTNER_TELE,A.PARTNER_ADDR
from FUN_PARTNER A

    left join FUN_DEPTS B on A.ID=B.PARTNER_ID
    left join FUN_USERS C on C.COMP_ID = A.COMP_ID and C.USER_ID = A.USER_ID

where A.COMP_ID = #{param.compId,jdbcType=INTEGER}

  and A.ID = #{param.id,jdbcType=INTEGER}


  and A.USER_ID = #{param.userId,jdbcType=INTEGER}


  and B.DEPT_ID = #{param.deptId,jdbcType=INTEGER}


  and A.JOIN_TIME >= #{param.startDate,jdbcType=TIMESTAMP}


  and A.JOIN_TIME <= #{param.endDate,jdbcType=TIMESTAMP}


  and( A.PARTNER_NAME like '%${param.fuzzySearch}%' or C.USER_NAME like '%${param.fuzzySearch}%' or B.DEPT_NAME like '%${param.fuzzySearch}%')

and A.IS_DEL = 0
order by A.JOIN_TIME desc
