mapper.delete:
delete from FUN_PHONE

	AND CITY_ID = #{cityId}
	AND COMP_ID = #{compId}
	AND AREA_ID = #{areaId}
	AND REG_ID = #{regId}
	AND DEPT_ID = #{deptId}
	AND CASE_ID = #{caseId}
	AND CASE_TYPE = #{caseType}
	AND PHONE = #{phone}
	AND CREATION_TIME = #{creationTime}
	AND USER_ID = #{userId}
	AND PHONE_SEC = #{phoneSec}
	AND FLAG = #{flag}

mapper.update:
UPDATE FUN_PHONE SET
AREA_ID = #{erpFunUsers.areaId}, REG_ID = #{erpFunUsers.regId}, DEPT_ID = #{erpFunUsers.deptId}, 
USER_ID = #{erpFunUsers.userId}, GR_ID = #{erpFunUsers.grId}
WHERE COMP_ID=#{erpFunUsers.compId} AND CASE_ID=#{caseId} AND CASE_TYPE=#{caseType}

mapper.select:
select PHONE phone,count(1) as phoneCount FROM FUN_PHONE WHERE COMP_ID=#{compId} AND CASE_TYPE=#{caseType} and PHONE in
#{phone}
group by PHONE
