mapper.select:

select 

from FUN_PROFIT
where PROFIT_ID = #{profitId,jdbcType=INTEGER}

  SET DATEFIRST 1; 
  select ${param.erpProfitequeryColumn} as dateCount,
  sum(a.PROFIT_MONEY) as profitCount 
  from FUN_PROFIT a left join FUN_DEAL b on a.DEAL_ID = b.DEAL_ID 
  where 
  	 a.COMP_ID = #{param.compId}

		and a.AREA_ID = #{param.serchRangeId}


		and a.REG_ID = #{param.serchRangeId}


		and a.DEPT_ID = #{param.serchRangeId}


		and a.GR_ID = #{param.serchRangeId}


		and a.USER_ID = #{param.serchRangeId}

	and b.SIGN_DATE >= #{param.startTime} and b.SIGN_DATE <=#{param.endTime}
	and b.DEAL_TYPE = #{param.caseType}
	group by ${param.erpProfitequeryColumn}

select convert(VARCHAR(7),B.SIGN_DATE,20) AS dateCount,SUM(A.PROFIT_MONEY) as actualProfit,SUM(A.RECEIVABLE_MONEY) as receivableProfit from FUN_PROFIT A left join FUN_DEAL B ON A.DEAL_ID = B.DEAL_ID where A.COMP_ID = #{param.compId}

	AND A.AREA_ID = #{param.serchRangeId}


	AND A.REG_ID = #{param.serchRangeId}


	AND A.DEPT_ID = #{param.serchRangeId}


	AND A.DEPT_ID = #{param.deptId}


	AND A.GR_ID = #{param.serchRangeId}


	AND A.ARCHIVE_ID = #{param.archiveId}

and B.SIGN_DATE >=#{param.startTime} and B.SIGN_DATE<=#{param.endTime} 
group by convert(VARCHAR(7),B.SIGN_DATE,20)

	WITH T_USER_COUNT AS (
	SELECT
		COMP_ID AS RANGE,
		COUNT (1) AS USER_COUNT
	FROM
		FUN_USERS
	WHERE
		COMP_ID = #{param.compId}

			AND AREA_ID = #{param.serchRangeId}


			AND REG_ID = #{param.serchRangeId}


		AND DEPT_ID = #{param.serchRangeId}


		AND DEPT_ID = #{param.deptId}


		AND GR_ID = #{param.serchRangeId}


		AND ARCHIVE_ID = #{param.archiveId}

	AND (
		JOIN_DEPT_TIME <= #{param.endTime}
		AND (
			USER_WRITEOFF = 0
			OR (
				USER_WRITEOFF != 0
				AND WRITEOFF_TIME > #{param.startTime}
			)
		)
	)
	GROUP BY
		COMP_ID
),
 T_PROFIT_COUNT AS (
	SELECT
		SUM (
			iif (
				b.DEAL_TYPE = 101,
				a.PROFIT_MONEY,
				0
			)
		) AS SALE_PROFIT_COUNT,
		SUM (
			iif (
				b.DEAL_TYPE = 102,
				a.PROFIT_MONEY,
				0
			)
		) AS LEASE_PROFIT_COUNT,
		A.COMP_ID AS RANGE
	FROM
		FUN_PROFIT a
	LEFT JOIN FUN_DEAL b ON a.DEAL_ID = b.DEAL_ID
	WHERE
		a.COMP_ID = #{param.compId}

		AND a.AREA_ID = #{param.serchRangeId}


		AND a.REG_ID = #{param.serchRangeId}


		AND a.DEPT_ID = #{param.serchRangeId}


		AND a.DEPT_ID = #{param.deptId}


		AND a.GR_ID = #{param.serchRangeId}


		AND a.ARCHIVE_ID = #{param.archiveId}


	AND b.SIGN_DATE >= #{param.startTime}
	AND b.SIGN_DATE <= #{param.endTime}
	GROUP BY
		A.COMP_ID
	) SELECT
		B.SALE_PROFIT_COUNT as saleProfitCount,
		B.LEASE_PROFIT_COUNT as leaseProfitCount,
		(
			ISNULL(B.SALE_PROFIT_COUNT, 0) / iif (
				ISNULL(A.USER_COUNT,0) = 0,
				1,
				ISNULL(A.USER_COUNT,0)
			)
		) AS perSaleProfitCount,
		(
			ISNULL(B.LEASE_PROFIT_COUNT, 0) / iif (
				ISNULL(A.USER_COUNT,0) = 0,
				1,
				ISNULL(A.USER_COUNT,0)
			)
		) AS perLeaseProfitCount
	FROM
		T_USER_COUNT A
	JOIN T_PROFIT_COUNT B ON A.RANGE = B.RANGE

   SELECT
 	top 15
		SUM (
			iif (
				b.DEAL_TYPE = 101,
				a.PROFIT_MONEY,
				0
			)
		) AS saleProfitCount,
		SUM (
			iif (
				b.DEAL_TYPE = 102,
				a.PROFIT_MONEY,
				0
			)
		) AS leaseProfitCount,
		SUM (
				a.PROFIT_MONEY
		) as totalProfitMoney,

			a.DEPT_ID as deptId


			a.DEPT_ID as deptId,a.USER_ID as userId

	FROM
		FUN_PROFIT a
	LEFT JOIN FUN_DEAL b ON a.DEAL_ID = b.DEAL_ID
	WHERE
		a.COMP_ID = #{param.compId}

		AND a.AREA_ID = #{param.serchRangeId}


		AND a.REG_ID = #{param.serchRangeId}


		AND a.DEPT_ID = #{param.serchRangeId}


		AND a.DEPT_ID = #{param.deptId}


		AND a.GR_ID = #{param.serchRangeId}


		AND a.ARCHIVE_ID = #{param.archiveId}

	AND b.SIGN_DATE >= #{param.startTime}
	AND b.SIGN_DATE <= #{param.endTime}

		AND a.USER_ID>0

	GROUP BY

			a.DEPT_ID


			a.DEPT_ID,a.USER_ID

	having SUM (
				a.PROFIT_MONEY
		)>0
	ORDER BY  totalProfitMoney desc

   SELECT
		SUM (
				a.PROFIT_MONEY
		) as totalProfitMoney
	FROM
		FUN_PROFIT a
	LEFT JOIN FUN_DEAL b ON a.DEAL_ID = b.DEAL_ID
	WHERE
		a.COMP_ID = #{param.compId}

		AND a.AREA_ID = #{param.serchRangeId}


		AND a.REG_ID = #{param.serchRangeId}


		AND a.DEPT_ID = #{param.serchRangeId}


		AND a.DEPT_ID = #{param.deptId}


		AND a.GR_ID = #{param.serchRangeId}


		AND a.ARCHIVE_ID = #{param.archiveId}


		AND b.DEAL_TYPE = 101


		AND b.DEAL_TYPE = 102

	AND b.${param.dateType} <= #{param.endTime}
	AND b.${param.dateType} >= #{param.startTime}

WITH T_USER_COUNT AS (
	SELECT
		COMP_ID 
		,AREA_ID
		,REG_ID 
		,DEPT_ID
		,GR_ID 
		,USER_ID as RANGE
		,COUNT (1) AS USER_COUNT
	FROM
		FUN_USERS
	WHERE
	COMP_ID =#{param.serchRangeId} 
		AND AREA_ID =#{param.serchRangeId}
		AND REG_ID =#{param.serchRangeId}
		AND DEPT_ID =#{param.serchRangeId}
		AND GR_ID =#{param.serchRangeId}
		AND USER_ID =#{param.serchRangeId}
	AND (
		user_job_date < #{param.startTime}
		AND (
			USER_WRITEOFF = 0
			OR (
				USER_WRITEOFF != 0
				AND WRITEOFF_TIME > #{param.endTime}
			)
		)
	)
	GROUP BY
		COMP_ID 
		,AREA_ID
		,REG_ID 
		,DEPT_ID
		,GR_ID 
		,USER_ID
),
T_PROFIT_COUNT AS (
	SELECT
		b.DEAL_TYPE,
		SUM (
			iif (
				b.DEAL_TYPE = 101,
				a.PROFIT_MONEY,
				0
			)
		) AS SALE_PROFIT_COUNT,
		SUM (
			iif (
				b.DEAL_TYPE = 102,
				a.PROFIT_MONEY,
				0
			)
		) AS LEASE_PROFIT_COUNT,
		a.COMP_ID 
		,a.AREA_ID
		,a.REG_ID 
		a.DEPT_ID
		,a.GR_ID 
		,a.USER_ID AS RANGE
	FROM
		FUN_PROFIT a
	LEFT JOIN FUN_DEAL b ON a.DEAL_ID = b.DEAL_ID
	WHERE
		a.COMP_ID =#{param.serchRangeId} 
		AND a.AREA_ID =#{param.serchRangeId}
		AND a.REG_ID =#{param.serchRangeId}
		AND a.DEPT_ID =#{param.serchRangeId}
		AND a.GR_ID =#{param.serchRangeId}
		AND a.USER_ID =#{param.serchRangeId}
	AND a.CREATE_DATE >= #{param.stratTime}
	AND a.CREATE_DATE <= #{param.endTime}
	GROUP BY
		b.DEAL_TYPE,
		a.COMP_ID 
		,a.AREA_ID
		,a.REG_ID 
		a.DEPT_ID
		,a.GR_ID 
		,a.USER_ID
) SELECT
	B.SALE_PROFIT_COUNT,
	B.LEASE_PROFIT_COUNT,
	(
		ISNULL(B.SALE_PROFIT_COUNT, 0) / iif (
			A.USER_COUNT = 0,
			1,
			A.USER_COUNT
		)
	) AS PERSALE_PROFIT_COUNT,
	(
		ISNULL(B.LEASE_PROFIT_COUNT, 0) / iif (
			A.USER_COUNT = 0,
			1,
			A.USER_COUNT
		)
	) AS PERLEASE_PROFIT_COUNT
FROM
	T_USER_COUNT A
JOIN T_PROFIT_COUNT B ON A.RANGE = B.RANGE
where B.DEAL_TYPE = #{param.caseType}

select top 10 d.USER_ID as userId ,d.USER_NAME AS userName,b.ARCHIVE_ID AS archiveId,d.DEPT_ID AS deptId,SUM(B.RECEIVABLE_MONEY) as salaryTotal from FUN_PROFIT B left join FUN_DEAL C ON B.DEAL_ID = C.DEAL_ID LEFT JOIN FUN_USERS d on b.ARCHIVE_ID = d.ARCHIVE_ID AND b.COMP_ID = d.COMP_ID where C.SIGN_DATE <= #{param.endTime} and C.SIGN_DATE >= #{param.startTime} and B.USER_ID>0 AND d.USER_WRITEOFF = 0
and b.COMP_ID = #{param.compId}
and b.COMP_ID = #{param.compId} AND B.DEPT_ID = #{param.deptId}
GROUP BY d.USER_ID,d.USER_NAME,d.DEPT_ID ,b.ARCHIVE_ID having SUM (B.RECEIVABLE_MONEY) >0 ORDER BY salaryTotal DESC, d.USER_ID asc

with t_profit_rank as ( SELECT b.ARCHIVE_ID AS archiveId,d.USER_NAME as userName,d.DEPT_ID as deptId, SUM (B.RECEIVABLE_MONEY) AS salaryTotal FROM FUN_PROFIT B LEFT JOIN FUN_DEAL C ON B.DEAL_ID = C.DEAL_ID LEFT JOIN FUN_USERS d ON b.ARCHIVE_ID = d.ARCHIVE_ID and b.COMP_ID = d.COMP_ID WHERE
b.COMP_ID = #{param.compId}
b.COMP_ID = #{param.compId} AND B.DEPT_ID = #{param.deptId}
and b.USER_ID>0 and C.SIGN_DATE <= #{param.endTime} and C.SIGN_DATE >= #{param.startTime} AND d.USER_WRITEOFF = 0 GROUP BY b.ARCHIVE_ID,d.USER_NAME,d.DEPT_ID having SUM (B.RECEIVABLE_MONEY) >0 ),t_profit_ranknum as( select archiveId,deptId,salaryTotal,ROW_NUMBER() over(order by salaryTotal DESC,archiveId) as rankNum from t_profit_rank ) select rankNum,salaryTotal from t_profit_ranknum where archiveId =#{param.archiveId}

with t_profit_rank as ( SELECT b.ARCHIVE_ID as archiveId,d.USER_NAME as userName,d.DEPT_ID as deptId , SUM (B.RECEIVABLE_MONEY) AS salaryTotal FROM FUN_PROFIT B LEFT JOIN FUN_DEAL C ON B.DEAL_ID = C.DEAL_ID LEFT JOIN FUN_USERS d ON b.ARCHIVE_ID = d.ARCHIVE_ID and b.COMP_ID = d.COMP_ID where
b.COMP_ID = #{param.compId}
b.COMP_ID = #{param.compId} AND B.DEPT_ID = #{param.deptId}
and b.USER_ID>0 and C.SIGN_DATE <= #{param.endTime} and C.SIGN_DATE >= #{param.startTime} AND d.USER_WRITEOFF = 0 GROUP BY b.ARCHIVE_ID,d.USER_NAME,d.DEPT_ID having SUM (B.RECEIVABLE_MONEY) >0 ),t_profit_ranknum as( select archiveId,deptId,userName,salaryTotal,ROW_NUMBER() over(order by salaryTotal DESC,archiveId) as rankNum from t_profit_rank ) select top 1 salaryTotal,rankNum from t_profit_ranknum where rankNum <=#{param.rankNumBefore} and salaryTotal >#{param.profit} order by rankNum desc

  	select SUM (B.PROFIT_MONEY)  FROM
				FUN_PROFIT B
			LEFT JOIN FUN_DEAL C ON B.DEAL_ID = C.DEAL_ID
			where
			b.COMP_ID =  #{compId}
			and b.${rangeColumn} in 

				#{rangeId}

			and b.USER_ID>0 and C.SIGN_DATE <= #{endTime} and C.SIGN_DATE >= #{startTime} 
			having SUM (B.PROFIT_MONEY) >0

WITH t_personCount AS ( SELECT COUNT (1) AS PERSON_COUNT, COMP_ID FROM FUN_USERS WHERE COMP_ID = #{compId} AND ${rangeColumn} IN
#{rangeId}
AND USER_JOB_DATE < #{endTime} AND ( USER_WRITEOFF = 0 OR ( USER_WRITEOFF != 0 AND WRITEOFF_TIME >= #{startTime} ) ) GROUP BY COMP_ID ), t_profitMoney AS ( SELECT SUM (A.PROFIT_MONEY) AS PROFIT_MONEY, A.COMP_ID FROM FUN_PROFIT A LEFT JOIN FUN_DEAL B ON A.DEAL_ID = B.DEAL_ID WHERE A.COMP_ID = #{compId} AND A.${rangeColumn} IN
#{rangeId}
AND B.SIGN_DATE <= #{endTime} AND B.SIGN_DATE >= #{startTime} AND A.USER_ID > 0 GROUP BY A.COMP_ID HAVING SUM (A.PROFIT_MONEY) >0 ) SELECT ( ISNULL(A.PROFIT_MONEY, 0) + 0.0 ) / iif ( B.PERSON_COUNT = 0, 1, B.PERSON_COUNT ) AS PROFIT_COUNT FROM t_profitMoney A JOIN t_personCount B ON A.COMP_ID = A.COMP_ID

	SELECT A.PROFIT_ID, A.COMP_ID, A.DEAL_ID, A.DEPT_ID, A.USER_ID, A.PROFIT_PROPORTION, A.PROFIT_MONEY, A.SETTLE_TIME,
    A.SETTLE_FLAG, A.REMARK, A.AGREEMENT_NO, A.CREATE_DATE, A.DEAL_VERIFY_TIME, A.DEAL_TYPE, A.AREA_ID,
    A.REG_ID, A.PROFIT_DEAL_TYPE, A.PERFORMANCE_ID, A.PERFORMANCE_NAME, A.GR_ID, A.PROFIT_TYPE, A.ARCHIVE_ID,
    A.ROLE_ID, A.ROLE_NAME, A.LEVELS_ID, A.LEVELS_NAME, A.RECEIVABLE_MONEY, A.PERFORMANCE_TYPE, A.DISCOUNT,
    A.ASSESSMENT_MONTH, A.VALID_FLAG,B.SELL_ADDRESS,C.USER_NAME FROM FUN_PROFIT A JOIN FUN_DEAL B ON A.DEAL_ID = B.DEAL_ID
	JOIN FUN_USERS C ON A.USER_ID = C.USER_ID 
	WHERE 
	A.COMP_ID =#{param.compId} AND A.PROFIT_TYPE = #{param.profitType}
	AND A.DEPT_ID =#{param.deptId }
	AND A.USER_ID =#{param.userId}
	AND A.REG_ID =#{param.regId}
	AND A.AREA_ID =#{param.areaId}
	AND A.GR_ID =#{param.grId}
	AND A.VALID_FLAG =#{param.validFlag}
	ORDER BY A.CREATE_DATE

