SELECT DISTINCT A.CITY_ID, A.SALE_ID AS HOUSE_ID, A.SALE_NO AS HOUSE_NO, A.BUILD_NAME AS HOUSE_NAME, A.SALE_DIRECT AS HOUSE_DIRECT, A.SALE_YEAR AS HOUSE_YEAR, A.SALE_FLOOR AS HOUSE_FLOOR, A.SALE_FLOORS AS HOUSE_FLOORS, A.SALE_ROOM AS HOUSE_ROOM, A.SALE_HALL AS HOUSE_HALL, A.SALE_WEI AS HOUSE_WEI, A.SALE_AREA AS HOUSE_AREA, SALE_USEAGE AS HOUSE_USEAGE, A.SALE_TOTAL_PRICE AS HOUSE_TOTAL_PRICE, A.SALE_UNIT_PRICE AS HOUSE_UNIT_PRICE, A.SALE_FITMENT AS HOUSE_FITMENT, A.SALE_SUBJECT AS HOUSE_SUBJECT, B.CHECK_STATUS AS CHECK_STATUS, B.PUBLISH_SITE AS PUBLISH_SITE, A.CREATION_TIME AS CREATION_TIME, B.UPDATE_TIME AS UPDATE_TIME, B.CHECK_USER AS CHECK_USER, B.PUB_ID AS PUB_ID FROM FUN_SALE A RIGHT JOIN FUN_HOUSE_PUBLISH_STATUS B ON A.SALE_ID = B.HOUSE_ID AND B.SALE_LEASE = 1 AND A.SALE_USEAGE = B.HOUSE_USEAGE WHERE 1 = 1 AND USER_ID IS NOT NULL AND SALE_STATUS = 1
and (A.BUILD_NAME like '%${house.buildText}%' or A.BUILD_CODE like '%${house.buildText}%')
and B.CHECK_USER = #{house.crmUser}
and B.PUBLISH_SITE IN (-1,4,5)
and B.PUBLISH_SITE = #{house.publishId}
and A.SALE_NO = #{house.houseNo}
and A.USER_ID = #{house.userid}
order by A.CREATION_TIME DESC

