mapper.select:
SELECT

FROM FUN_STREET
WHERE IS_DEL = 0
-- 1=街道地区(例如华南地区) 2=街道省份 3=街道城市 4=街道行政区


    AND STREET_TERRITORY_ID = #{param.dataId}


    AND STREET_PROVINCE_ID = #{param.dataId}


    AND STREET_CITY_ID = #{param.dataId}



  AND STREET_NAME LIKE '%${param.streetName}%'

ORDER BY SEQ_NO ASC
