mapper.select:
select top 15
${param.dimension}_ID,
SUM ( iif(SALE_LEASE=1, H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK ,0) ) AS SALE_TOTAL_CLICK, SUM ( iif(SALE_LEASE=2, H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK ,0) ) AS LEASE_TOTAL_CLICK, SUM ( H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK ) AS TOTAL_CLICK from T_HOUSE_TRENDS WHERE COMP_ID = #{param.compId} AND CITY_ID = #{param.cityId}
AND ${param.serchRange}_ID = #{param.serchRangeId}
AND DATA_DATE >= #{param.startTime} AND DATA_DATE < #{param.endTime}
AND USER_ID>0
group by ${param.dimension}_ID
ORDER BY TOTAL_CLICK desc

SELECT SUM(H7_CLICK) H7_CLICK,SUM(H8_CLICK) H8_CLICK,SUM(H9_CLICK) H9_CLICK,SUM(H10_CLICK) H10_CLICK,SUM(H11_CLICK) H11_CLICK,SUM(H12_CLICK) H12_CLICK,SUM(H13_CLICK) H13_CLICK,SUM(H14_CLICK) H14_CLICK,
SUM(H15_CLICK) H15_CLICK,SUM(H16_CLICK) H16_CLICK,SUM(H17_CLICK) H17_CLICK,SUM(H18_CLICK) H18_CLICK,SUM(H19_CLICK) H19_CLICK,SUM(H20_CLICK) H20_CLICK,SUM(H21_CLICK) H21_CLICK,SUM(H22_CLICK) H22_CLICK
FROM T_HOUSE_TRENDS
WHERE CITY_ID = #{param.cityId} 
AND NEW_FLAG = 1
AND DATA_DATE >= #{param.startTime} AND DATA_DATE <= #{param.endTime} 
AND COMP_ID = #{param.compId}

	AND AREA_ID = #{param.serchRangeId}


	AND REG_ID = #{param.serchRangeId}


	AND DEPT_ID = #{param.serchRangeId}


	AND GR_ID = #{param.serchRangeId}


	AND USER_ID = #{param.serchRangeId}

SELECT 
	SUM (H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK +
	 H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK 
	+ H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK
	) AS VALUE
	,CONVERT (VARCHAR (10),DATA_DATE,120) AS NAME
FROM T_HOUSE_TRENDS 
WHERE CITY_ID = #{param.cityId} AND NEW_FLAG = 1 
	AND DATA_DATE >= #{param.startTime} AND DATA_DATE <= #{param.endTime} 
	AND COMP_ID = #{param.compId}

		AND AREA_ID = #{param.serchRangeId}


		AND REG_ID = #{param.serchRangeId}


		AND DEPT_ID = #{param.serchRangeId}


		AND GR_ID = #{param.serchRangeId}


		AND USER_ID = #{param.serchRangeId}

group by CONVERT (VARCHAR (10),DATA_DATE,120)
ORDER BY VALUE DESC

SELECT b.SITE_NAME as NAME,SUM(H7_CLICK+H8_CLICK+H9_CLICK+H10_CLICK+H11_CLICK+H12_CLICK+H13_CLICK+H14_CLICK+H15_CLICK+H16_CLICK+H17_CLICK+H18_CLICK+H19_CLICK+H20_CLICK+H21_CLICK+H22_CLICK) AS VALUE
FROM T_HOUSE_TRENDS a 
LEFT JOIN  hft_admindb.dbo.WEB_SITE b ON a.SITE_ID = b.SITE_ID
WHERE a.CITY_ID = #{param.cityId} 
AND a.NEW_FLAG = 1 AND a.DATA_DATE >= #{param.startTime} AND a.DATA_DATE <= #{param.endTime} 
and a.COMP_ID = #{param.compId} and a.CITY_ID = #{param.cityId}

	and a.AREA_ID = #{param.serchRangeId}


	and a.REG_ID = #{param.serchRangeId}


	and a.DEPT_ID = #{param.serchRangeId}


	and a.GR_ID = #{param.serchRangeId}


	and a.USER_ID = #{param.serchRangeId}

GROUP BY b.SITE_NAME

select top 10
COMP_ID
COMP_ID,AREA_ID
COMP_ID,AREA_ID,REG_ID
COMP_ID,AREA_ID,REG_ID,DEPT_ID
COMP_ID,AREA_ID,REG_ID,DEPT_ID,GR_ID
COMP_ID,AREA_ID,REG_ID,DEPT_ID,GR_ID,USER_ID
, SUM(H7_CLICK+H8_CLICK+H9_CLICK+H10_CLICK+H11_CLICK+H12_CLICK+H13_CLICK+H14_CLICK+H15_CLICK+H16_CLICK+H17_CLICK+H18_CLICK+H19_CLICK+H20_CLICK+H21_CLICK+H22_CLICK) AS VALUE
from T_HOUSE_TRENDS 
where CITY_ID = #{param.cityId} 
AND NEW_FLAG = 1 and
DATA_DATE >= #{param.startTime} AND DATA_DATE <= #{param.endTime} 
AND COMP_ID = #{param.compId}

	and AREA_ID = #{param.serchRangeId}


	and REG_ID = #{param.serchRangeId}


	and DEPT_ID = #{param.serchRangeId}


	and GR_ID = #{param.serchRangeId}


	and USER_ID = #{param.serchRangeId}

group by 
COMP_ID
COMP_ID,AREA_ID
COMP_ID,AREA_ID,REG_ID
COMP_ID,AREA_ID,REG_ID,DEPT_ID
COMP_ID,AREA_ID,REG_ID,DEPT_ID,GR_ID
COMP_ID,AREA_ID,REG_ID,DEPT_ID,GR_ID,USER_ID
ORDER BY VALUE DESC

SELECT top 10

	HOUSE_ROOM AS NAME


	 HOUSE_REG AS NAME


	HOUSE_FITMENT AS NAME


	 HOUSE_TYPE AS NAME

, SUM(H7_CLICK+H8_CLICK+H9_CLICK+H10_CLICK+H11_CLICK+H12_CLICK+H13_CLICK+H14_CLICK+H15_CLICK+H16_CLICK+H17_CLICK+H18_CLICK+H19_CLICK+H20_CLICK+H21_CLICK+H22_CLICK) AS VALUE
FROM  T_HOUSE_TRENDS 
WHERE  CITY_ID = #{param.cityId} AND NEW_FLAG = 1
AND DATA_DATE >= #{param.startTime} AND DATA_DATE <= #{param.endTime} 
and COMP_ID = #{param.compId}

	and AREA_ID = #{param.serchRangeId}


	and REG_ID = #{param.serchRangeId}


	and DEPT_ID = #{param.serchRangeId}


	and GR_ID = #{param.serchRangeId}


	and USER_ID = #{param.serchRangeId}

group by 

	HOUSE_ROOM


	 HOUSE_REG


	HOUSE_FITMENT


	 HOUSE_TYPE

ORDER BY VALUE DESC

WITH t_site_rank AS (
	SELECT TOP 5 SITE_ID,SUM (H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK) AS TOTAL_CLICK
	FROM
		T_HOUSE_TRENDS
	WHERE
		COMP_ID = #{param.compId} and CITY_ID = #{param.cityId}

		and AREA_ID = #{param.serchRangeId}


		and REG_ID = #{param.serchRangeId}


		and DEPT_ID = #{param.serchRangeId}


		and GR_ID = #{param.serchRangeId}


		and USER_ID = #{param.serchRangeId}

	AND DATA_DATE >= #{param.startTime} AND DATA_DATE <= #{param.endTime} AND NEW_FLAG = 1 
	GROUP BY SITE_ID ORDER BY TOTAL_CLICK DESC
) SELECT
	TOP 200 a.SALE_LEASE AS saleLease,
	a.HOUSE_ID as houseId,

		${param.column}

	SUM (iif(b.site_id IS NULL, H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK, 0)) AS clickOther,
	SUM (H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK) AS totalClick,
	SUM (1) AS total
FROM T_HOUSE_TRENDS a
LEFT JOIN t_site_rank b ON a.site_id = b.site_id
WHERE
COMP_ID = #{param.compId} and NEW_FLAG = 1 and  CITY_ID = #{param.cityId}

	and AREA_ID = #{param.serchRangeId}


	and REG_ID = #{param.serchRangeId}


	and DEPT_ID = #{param.serchRangeId}


	and GR_ID = #{param.serchRangeId}


	and USER_ID = #{param.serchRangeId}

	AND DATA_DATE >= #{param.startTime} AND DATA_DATE <= #{param.endTime} AND NEW_FLAG = 1 
and SALE_LEASE = 1
and SALE_LEASE = 2
GROUP BY a.SALE_LEASE, a.HOUSE_ID
ORDER BY totalClick DESC

		SELECT top 5 SITE_ID as siteId,
		SUM(H7_CLICK+H8_CLICK+H9_CLICK+H10_CLICK+H11_CLICK+H12_CLICK+H13_CLICK+H14_CLICK+H15_CLICK+H16_CLICK+H17_CLICK+H18_CLICK+H19_CLICK+H20_CLICK+H21_CLICK+H22_CLICK) AS TOTAL_CLICK , MAX(SALE_LEASE) AS SALE_LEASE FROM T_HOUSE_TRENDS 
		WHERE COMP_ID = #{param.compId} and CITY_ID = #{param.cityId}

			and AREA_ID = #{param.serchRangeId}


			and REG_ID = #{param.serchRangeId}


			and DEPT_ID = #{param.serchRangeId}


			and GR_ID = #{param.serchRangeId}


			and USER_ID = #{param.serchRangeId}


          and ${param.selectColumn} = #{param.selectColumnId}

		AND DATA_DATE >= #{param.startTime} AND DATA_DATE <= #{param.endTime} AND NEW_FLAG = 1 
		and SALE_LEASE = 1
		and SALE_LEASE = 2
		GROUP BY SITE_ID

		WITH T_SITE_RANK AS (
			SELECT TOP 5 SITE_ID,SUM (H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK) AS TOTAL_CLICK
			FROM T_HOUSE_TRENDS
			WHERE NEW_FLAG = 1 AND  CITY_ID = #{param.cityId} and
			COMP_ID = #{param.compId}

				and AREA_ID = #{param.serchRangeId}


				and REG_ID = #{param.serchRangeId}


				and DEPT_ID = #{param.serchRangeId}


				and GR_ID = #{param.serchRangeId}


				and USER_ID = #{param.serchRangeId}


              and ${param.selectColumn} = #{param.selectColumnId}

			AND DATA_DATE >= #{param.startTime} AND DATA_DATE <= #{param.endTime} AND NEW_FLAG = 1 
			and SALE_LEASE = 1
			and SALE_LEASE = 2
			GROUP BY SITE_ID
			ORDER BY TOTAL_CLICK DESC
		) 
		SELECT TOP 200 
		a.COMP_ID AS compId
		a.COMP_ID AS compId,a.AREA_ID AS areaId
		a.COMP_ID AS compId,a.AREA_ID AS areaId,a.REG_ID AS regId
		a.COMP_ID AS compId,a.AREA_ID AS areaId,a.REG_ID AS regId,a.DEPT_ID AS deptId
		a.COMP_ID AS compId,a.AREA_ID AS areaId,a.REG_ID AS regId,a.DEPT_ID AS deptId,a.GR_ID AS grId
		a.COMP_ID AS compId,a.AREA_ID AS areaId,a.REG_ID AS regId,a.DEPT_ID AS deptId,a.GR_ID AS grId,a.USER_ID AS userId
		${param.dimensionColumnSql}

		,${param.column}

		SUM (iif(b.site_id IS NULL, H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK, 0)) AS clickOther,
		SUM (H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK) AS totalClick,
		SUM (1) AS total
		FROM T_HOUSE_TRENDS a
		LEFT JOIN t_site_rank b ON a.site_id = b.site_id 
		WHERE
		a.COMP_ID = #{param.compId} and CITY_ID = #{param.cityId}
		AND DATA_DATE >= #{param.startTime} AND DATA_DATE <= #{param.endTime} AND NEW_FLAG = 1 

			and a.AREA_ID = #{param.serchRangeId}


			and a.REG_ID = #{param.serchRangeId}


			and a.DEPT_ID = #{param.serchRangeId}


			and a.GR_ID = #{param.serchRangeId}


			and a.USER_ID = #{param.serchRangeId}


          and a.${param.rangeColumn} = #{param.serchRangeId}

		and SALE_LEASE = 1
		and SALE_LEASE = 2
		GROUP BY 
		a.COMP_ID
		a.COMP_ID,a.AREA_ID
		a.COMP_ID,a.AREA_ID,a.REG_ID
		a.COMP_ID,a.AREA_ID,a.REG_ID,a.DEPT_ID
		a.COMP_ID,a.AREA_ID,a.REG_ID,a.DEPT_ID,a.GR_ID
		a.COMP_ID,a.AREA_ID,a.REG_ID,a.DEPT_ID,a.GR_ID,a.USER_ID
        ${param.dimensionGroupSqlLow}
		order by totalClick desc

WITH t_site_rank AS (
	SELECT TOP 5 SITE_ID,SUM (H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK) AS TOTAL_CLICK
	FROM T_HOUSE_TRENDS
	WHERE 
	COMP_ID = #{param.compId} and CITY_ID = #{param.cityId}

		and AREA_ID = #{param.serchRangeId}


		and REG_ID = #{param.serchRangeId}


		and DEPT_ID = #{param.serchRangeId}


		and GR_ID = #{param.serchRangeId}


		and USER_ID = #{param.serchRangeId}

	AND DATA_DATE >= #{param.startTime} AND DATA_DATE <= #{param.endTime} AND NEW_FLAG = 1 
	and SALE_LEASE = 1
	and SALE_LEASE = 2
	GROUP BY SITE_ID ORDER BY TOTAL_CLICK DESC
) 
SELECT TOP 200

	a.HOUSE_ROOM as primaryName,


	a.HOUSE_REG as primaryId,


	a.HOUSE_FITMENT as primaryName,


	a.HOUSE_TYPE as primaryName,


${param.column}

SUM (iif(b.site_id IS NULL, H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK, 0)) AS clickOther,
SUM (H7_CLICK + H8_CLICK + H9_CLICK + H10_CLICK + H11_CLICK + H12_CLICK + H13_CLICK + H14_CLICK + H15_CLICK + H16_CLICK + H17_CLICK + H18_CLICK + H19_CLICK + H20_CLICK + H21_CLICK + H22_CLICK) AS totalClick,
SUM (1) AS total
FROM
	T_HOUSE_TRENDS a
LEFT JOIN t_site_rank b ON a.site_id = b.site_id
WHERE
CITY_ID = #{param.cityId} AND NEW_FLAG = 1 and COMP_ID = #{param.compId}

	and a.AREA_ID = #{param.serchRangeId}


	and a.REG_ID = #{param.serchRangeId}


	and a.DEPT_ID = #{param.serchRangeId}


	and a.GR_ID = #{param.serchRangeId}


	and a.USER_ID = #{param.serchRangeId}

AND DATA_DATE >= #{param.startTime} AND DATA_DATE <= #{param.endTime} AND NEW_FLAG = 1 
and SALE_LEASE = 1
and SALE_LEASE = 2
GROUP BY 

	a.HOUSE_ROOM


	 a.HOUSE_REG


	a.HOUSE_FITMENT


	 a.HOUSE_TYPE

order by totalClick desc
