select ISNULL(SUM (INFLUENCE_SCORE), 0) from FUN_USER_SCORE_RECORD
WHERE CITY_ID = #{cityId} AND COMP_ID =#{compId}

	AND CREATE_TIME >=#{startTime}


	AND CREATE_TIME <= #{endTime}


	AND ARCHIVE_ID = #{archiveId}

select ARCHIVE_ID archiveId ,ISNULL(SUM (INFLUENCE_SCORE), 0) scoreCount
from FUN_USER_SCORE_RECORD
WHERE CITY_ID = #{cityId} AND  COMP_ID =#{compId}

	AND CREATE_TIME >=#{startTime}


	AND CREATE_TIME <= #{endTime}

GROUP BY ARCHIVE_ID

